"use client"

import { useTheme } from "next-themes"
import { Toaster as Sonner } from "sonner"

type ToasterProps = React.ComponentProps<typeof Sonner>

const Toaster = ({ ...props }: ToasterProps) => {
  const { theme = "system" } = useTheme()

  return (
    <Sonner
      theme={theme as ToasterProps["theme"]}
      className="toaster group"
      toastOptions={{
        classNames: {
          toast:
            "group toast group-[.toaster]:bg-background group-[.toaster]:text-foreground group-[.toaster]:border-border group-[.toaster]:shadow-lg",
          description: "group-[.toast]:text-muted-foreground",
          actionButton:
            "group-[.toast]:bg-primary group-[.toast]:text-primary-foreground",
          cancelButton:
            "group-[.toast]:bg-muted group-[.toast]:text-muted-foreground",
        },
      }}
      {...props}
    />
  )
}

export { Toaster }

client/src/index.css.content:
@import "tailwindcss";
@import "tw-animate-css";

@theme inline {
  /* Bright Colorful Palette */
  --color-background: hsl(180 30% 85%); /* Light teal/mint */
  --color-foreground: hsl(220 30% 15%); /* Dark blue-gray */

  --color-primary: hsl(150 70% 45%); /* Bright teal green */
  --color-primary-foreground: hsl(0 0% 100%);

  --color-secondary: hsl(45 100% 55%); /* Bright yellow */
  --color-secondary-foreground: hsl(0 0% 0%);

  --color-accent: hsl(280 70% 60%); /* Purple */
  --color-accent-foreground: hsl(0 0% 100%);

  --color-destructive: hsl(0 80% 55%); /* Bright red */
  --color-destructive-foreground: hsl(0 0% 100%);

  --color-card: hsl(200 40% 95%); /* Light blue-white */
  --color-card-foreground: hsl(220 30% 15%);

  --color-popover: hsl(200 40% 92%);
  --color-popover-foreground: hsl(220 30% 15%);

  --color-border: hsl(200 30% 70%);
  --color-input: hsl(200 30% 90%);
  --color-ring: hsl(150 70% 45%);

  /* Retro Pixel Fonts */
  --font-display: "Press Start 2P", monospace;
  --font-ui: "VT323", monospace;
  --font-sans: "VT323", monospace;

  /* Rounded corners for playful look */
  --radius-sm: 4px;
  --radius-md: 8px;
  --radius-lg: 12px;
}

@layer base {
  * {
    border-color: var(--border);
    box-sizing: border-box;
  }
  body {
    background-color: var(--color-background);
    color: var(--color-foreground);
    font-family: var(--font-sans);
    overflow: hidden;
    touch-action: none;
    font-size: 18px;
  }

  h1, h2, h3, h4, h5, h6 {
    font-family: var(--font-display);
    text-transform: uppercase;
    letter-spacing: 0.02em;
  }

  button {
    font-family: var(--font-display);
  }
}

@layer utilities {
  .font-display { font-family: var(--font-display); }
  .font-ui { font-family: var(--font-ui); font-size: 24px; }

  /* Retro pixel text shadow */
  .text-shadow-pixel {
    text-shadow: 2px 2px 0px rgba(0,0,0,0.3);
  }

  /* Pixel border box */
  .pixel-border {
    border: 3px solid;
    border-color: hsl(200 40% 80%) hsl(200 30% 50%) hsl(200 30% 50%) hsl(200 40% 80%);
    border-radius: 8px;
  }

  /* Bright panel style */
  .block-panel {
    background: linear-gradient(180deg, hsl(200 50% 98%) 0%, hsl(200 40% 92%) 100%);
    border: 3px solid;
    border-color: hsl(200 50% 85%) hsl(200 30% 60%) hsl(200 30% 60%) hsl(200 50% 85%);
    border-radius: 8px;
  }

  /* Retro button style */
  .pixel-btn {
    background: linear-gradient(180deg, hsl(150 65% 55%) 0%, hsl(150 70% 40%) 100%);
    border: 3px solid;
    border-color: hsl(150 60% 70%) hsl(150 75% 25%) hsl(150 75% 25%) hsl(150 60% 70%);
    color: #fff;
    font-weight: bold;
    transition: all 0.1s;
    border-radius: 6px;
  }

  .pixel-btn:hover {
    transform: translate(1px, 1px);
    background: linear-gradient(180deg, hsl(150 65% 60%) 0%, hsl(150 70% 45%) 100%);
  }

  .pixel-btn:active {
    transform: translate(2px, 2px);
  }

  /* Subtle scanline effect */
  .scanlines::after {
    content: "";
    position: absolute;
    inset: 0;
    background: repeating-linear-gradient(
      0deg,
      rgba(0, 0, 0, 0.03) 0px,
      rgba(0, 0, 0, 0.03) 1px,
      transparent 1px,
      transparent 2px
    );
    pointer-events: none;
  }

  /* Playful grid background */
  .grid-bg {
    background-image: 
      linear-gradient(rgba(100,180,180,0.15) 1px, transparent 1px),
      linear-gradient(90deg, rgba(100,180,180,0.15) 1px, transparent 1px);
    background-size: 40px 40px;
  }

  /* Floating animation */
  @keyframes float {
    0%, 100% { transform: translateY(0); }
    50% { transform: translateY(-8px); }
  }
  .animate-float {
    animation: float 3s ease-in-out infinite;
  }

  /* Pulse scale animation */
  @keyframes pulse-scale {
    0%, 100% { transform: scale(1); }
    50% { transform: scale(1.03); }
  }
  .animate-pulse-scale {
    animation: pulse-scale 2s ease-in-out infinite;
  }

  /* Game area styling */
  .game-area {
    background: linear-gradient(135deg, hsl(180 25% 92%) 0%, hsl(160 30% 88%) 50%, hsl(200 25% 90%) 100%);
    border-radius: 12px;
    border: 4px solid hsl(180 30% 70%);
  }
}

