    areaShrinkRate: 0,
  },
  {
    level: 2,
    name: 'Lab Sample',
    figuresCount: 10,
    startTime: 18,
    timeBonusPerFigure: 1.5,
    speedMultiplier: 1.2,
    sizeMultiplier: 1.0,
    areaShrinkRate: 0,
  },
  {
    level: 3,
    name: 'Culture Growth',
    figuresCount: 12,
    startTime: 16,
    timeBonusPerFigure: 1.5,
    speedMultiplier: 1.4,
    sizeMultiplier: 1.0,
    areaShrinkRate: 0.5,
  },
  {
    level: 4,
    name: 'Outbreak',
    figuresCount: 14,
    startTime: 15,
    timeBonusPerFigure: 1,
    speedMultiplier: 1.6,
    sizeMultiplier: 1.1,
    areaShrinkRate: 1,
  },
  {
    level: 5,
    name: 'Pandemic',
    figuresCount: 16,
    startTime: 14,
    timeBonusPerFigure: 1,
    speedMultiplier: 1.8,
    sizeMultiplier: 1.15,
    areaShrinkRate: 1.5,
  },
  {
    level: 6,
    name: 'Biohazard',
    figuresCount: 18,
    startTime: 13,
    timeBonusPerFigure: 0.8,
    speedMultiplier: 2.0,
    sizeMultiplier: 1.2,
    areaShrinkRate: 2,
  },
  {
    level: 7,
    name: 'Extinction',
    figuresCount: 20,
    startTime: 12,
    timeBonusPerFigure: 0.5,
    speedMultiplier: 2.2,
    sizeMultiplier: 1.25,
