    shapes: [
      // Blocky body
      { type: 'square', offsetX: 0, offsetY: 0, width: 48, height: 48, rotation: 3, color: '#7FD17F', strokeColor: '#4CAF50', strokeWidth: 3 },
      // Angry slanted eyes
